mapper.select:
 		SELECT COUNT(*) AS LIST_CNT
		FROM ${table}
		WHERE 1 = 1

			AND DEL_YN = #{delYn}


			AND (UPPER(MEMBER_ID) LIKE UPPER('%'||#{idKeyword}||'%')
			OR UPPER(MEMBER_NAME) LIKE UPPER('%'||#{nameKeyword}||'%'))

SELECT * FROM (SELECT ROW_NUMBER() OVER (ORDER BY MEMBER_ID) NUM , MEMBER_ID , MEMBER_NAME , MEMBER_GENDER , TO_CHAR(MEMBER_BIRTHDAY, 'YYYY.MM.DD') AS MEMBER_BIRTHDAY , MEMBER_ADDR , MEMBER_TEL , MEMBER_TEL2 , MEMBER_EMAIL , TO_CHAR(JOIN_DATE, 'YYYY.MM.DD') AS JOIN_DATE , DEL_YN FROM MEMBER_INFO WHERE 1 = 1
AND DEL_YN = #{delYn}
AND (UPPER(MEMBER_ID) LIKE UPPER('%'||#{idKeyword}||'%') OR UPPER(MEMBER_NAME) LIKE UPPER('%'||#{nameKeyword}||'%'))
ORDER BY MEMBER_ID ) WHERE NUM BETWEEN #{pagination.startList} AND #{pagination.listSize}
